import React, { Component } from 'react'
import { Input,Typography,Button} from 'antd';
const { TextArea } = Input;
const { Text } = Typography;

export default class NoteFrom extends Component {
    constructor(props) {
        super(props)

        this.state={
          title:'',
          content:''
        }
      }

    handleTitleChange = (e) =>{
        console.log(e.target.value)
    this.setState({
        title : e.target.value
    })
    }

    render() {
        return (
            <div style= {{width:'300px'}}>
                <Text strong>Title</Text>
                <Input style ={{marginBottom:'7px'}} placeholder="Title" 
                    onChange = {this.handleTitleChange}/>
                <Text strong>Description</Text>
                <TextArea style ={{marginTop:'7px',marginBottom:'7px'}} rows={4} placeholder="Type something"/>
                <Button style ={{marginTop:'7px',marginBottom:'7px' }} type="primary" size='large'>
                    Add
                </Button>
                <div style={{borderTopWidth:'thin'}}></div>
            </div>
        )
    }
}
