import React, { Component } from 'react'
import {Card ,Button} from 'antd'
import {connect} from 'react-redux'
import { removeNote } from '../redux/actions/actions'
import store from '../redux/store/store'

class NotesList extends Component {

    handleDelete =(id)=>() =>{
        this.props.removeNewNote(id) 
        localStorage.setItem('notes',JSON.stringify(store.getState()))
    }

    render() {
        return (
            <>
            {this.props.notes.map(note => (
                <Card 
                    key = {note.id}
                    title={note.title} 
                    extra={<Button onClick = {this.handleDelete(note.id)}>delete</Button>} 
                    bordered={true} 
                    style={{ width: 300 ,marginTop:'10px'}}
                    headStyle={{backgroundColor:'#ff5452'}}>
                    <p>
                        {note.content} 
                    </p>
                </Card>  
            ))}   
            </>                                
        )
    }
}
const mapStateToProps = function(state){
    return {
      notes :state.notes,
    }
  }

const mapDispatchToProps ={
    removeNewNote : removeNote 
}

export default connect(mapStateToProps,mapDispatchToProps)(NotesList)
